CREATE TABLE PLACES (PLACE_ID VARCHAR(255), NAME VARCHAR(255), OWNER_ID VARCHAR(255), VISIBILITY VARCHAR(255), LATITUDE DOUBLE(20,9), LONGITUDE DOUBLE (20,9));
CREATE TABLE COMMENTS (COMMENT_ID VARCHAR(255), TEXT VARCHAR(255), PLACE_ID VARCHAR(255), OWNER_ID VARCHAR(255));
CREATE TABLE USERS (USER_ID VARCHAR(255), USERNAME VARCHAR(255), PODID VARCHAR(255));
CREATE TABLE SCORES (SCORE_ID VARCHAR(255), SCORE DOUBLE(20,9), PLACE_ID VARCHAR(255), OWNER_ID VARCHAR(255));
CREATE TABLE PICTURES (PICTURE_ID VARCHAR(255), URL VARCHAR(255), PLACE_ID VARCHAR(255), OWNER_ID VARCHAR(255));